export const resets = `
  *, *:before, *:after {
    box-sizing: inherit;
    padding: 0;
    margin: 0;
  }

  html {
    box-sizing: border-box;
  }

  body {
    overflow-x: hidden;
    font-behaviour: smooth
  }

`;
